var adriftApp=angular.module("adriftApp",["ngRoute","staticControllers","userControllers","topicControllers"]);adriftApp.config(["$routeProvider","$locationProvider",function($routeProvider,$locationProvider){$locationProvider.html5Mode({enabled:true});$routeProvider.when("/welcome",{templateUrl:"partials/static/welcome.html",controller:"StaticWelcomeCtrl"}).when("/drifters",{templateUrl:"partials/users/index.html",controller:"UserIndexCtrl"}).when("/drifters/:userId",{templateUrl:"partials/users/show.html",controller:"UserShowCtrl"}).when("/locations",{templateUrl:"partials/topics/index.html",controller:"TopicIndexCtrl"}).when("/locations/:topicId",{templateUrl:"partials/topics/show.html",controller:"TopicShowCtrl"}).otherwise({redirectTo:"/welcome"})}]);// Static Controller
var staticControllers=angular.module("staticControllers",[]);staticControllers.controller("StaticWelcomeCtrl",["$scope",function($scope){}]);
// User Controller
var userControllers=angular.module("userControllers",[]);userControllers.controller("UserIndexCtrl",["$scope","$http",function($scope,$http){$http.get("users").success(function(data){console.log(data)})}]);userControllers.controller("UserShowCtrl",["$scope","$http","$routeParams",function($scope,$http,$routeParams){$http.get("users/"+$routeParams.userId).success(function(data){console.log(data)})}]);
// Topics Controller
var topicControllers=angular.module("topicControllers",[]);topicControllers.controller("TopicIndexCtrl",["$scope","$http",function($scope,$http){$http.get("topics/").success(function(data){console.log(data)})}]);topicControllers.controller("TopicShowCtrl",["$scope","$http","$routeParams",function($scope,$http,$routeParams){$http.get("topics/"+$routeParams.userId).success(function(data){console.log(data)})}]);